package tilemap8x8x256x256_test

import (
	"github.com/reiver/go-tilemap8x8x256x256"

	"github.com/reiver/go-rgba32"

	"bytes"
	"encoding/base64"
	"image"
	"image/png"
	"strings"

	"testing"
)

func TestTileMap_imageimage(t *testing.T) {

	// THIS IS WHAT ACTUALLY MATTERS!
	var datum image.Image = tilemap8x8x256x256.TileMap{}

	if nil == datum {
		t.Errorf("This should never happen.")
	}
}

func TestTileMap_At(t *testing.T) {

	var buffer [tilemap8x8x256x256.ByteSize]uint8

	var text tilemap8x8x256x256.TileMap = tilemap8x8x256x256.TileMap{
		Map: buffer[:],

		Tiles: func(index uint8)image.Image{
			var skyColor    [4]uint8 = [4]uint8{0x00,0xbf,0xff,0xff}
			var groundColor [4]uint8 = [4]uint8{0x9B,0x76,0x53,0xff}

			switch index {
			case 0: // sky
				return rgba32.Slice(skyColor[:])
			case 1: // ground
				return rgba32.Slice(groundColor[:])
			default:
				panic("unhandled index")
			}
		},
	}

	{
		const sky = 0
		const ground = 1

		for offset,_ := range buffer {
			buffer[offset] = sky
		}

		{
			const row = 31
			for column:=0;column<256;column++ {
				offset := row * tilemap8x8x256x256.MapWidth + column

				buffer[offset] = ground
			}
		}

	}

	var serialized strings.Builder
	{
		var pngBuffer bytes.Buffer

		err := png.Encode(&pngBuffer, text)
		if nil != err {
			t.Errorf("Did not expect an error, but actually got one.")
			t.Logf("ERROR: (%T) %q", err, err)
			return
		}

                encoded := base64.StdEncoding.EncodeToString(pngBuffer.Bytes())

		serialized.WriteString("IMAGE:")
                serialized.WriteString(encoded)
	}

	{
		const expected = "IMAGE:"

		actual := serialized.String()

		if expected != actual {
			t.Errorf("The actual image is not what was expected.")
			t.Logf("EXPECTED: %q", expected)
			t.Logf("ACTUAL:   %q", actual)
			return
		}
	}
}
